mapper.update:
    
		SELECT
			s.staff_code
		FROM 
			tb_staff AS s
		WHERE 
			s.member_id =#{member_id}
	 
	    UPDATE 
	    	tb_customer
		SET
			 customer_name=#{customer_name}
			,customer_tel=#{customer_tel}
			,customer_birth=#{customer_birth}
			,staff_code=#{staff_code}
		WHERE customer_code=#{customer_code};

mapper.select:
SELECT customer_code ,customer_name ,customer_tel ,customer_birth ,count(d.devopen_customer) ,count(h.homeopen_customer) ,m.member_name ,m.member_id ,st.store_name ,customer_reg_date ,s.staff_code FROM tb_customer AS c JOIN tb_staff AS s ON c.staff_code=s.staff_code JOIN tb_member AS m ON s.member_id=m.member_id join tb_store AS st ON s.store_code=st.store_code LEFT JOIN tb_devopen AS d ON d.staff_code=c.staff_code AND d.devopen_customer=c.customer_name LEFT JOIN tb_homeopen AS h ON h.staff_code = c.staff_code AND h.homeopen_customer=c.customer_name
(customer_reg_date between #{dateSch1} and #{dateSch2})
AND customer_name like #{table_search}
AND customer_tel like #{%table_search}
AND customer_birth like #{table_search}
AND m.member_name like #{table_search}
GROUP BY customer_code ORDER BY customer_reg_date DESC;

mapper.insert:
    
	    	select
	    	(case COUNT(*)
 			when 0 then 'customer_1'
	    	else concat('customer_',Max(cast(SUBSTR(customer_code,10) AS DECIMAL))+1)
	    	end) as customer_code
	    	from
	    	  	tb_customer as c 
	 
		    INSERT INTO tb_customer
			(customer_code, customer_name, customer_tel, customer_birth, staff_code, customer_reg_date)
			VALUES (#{customer_code}
					,#{customer_name}
					, #{customer_tel}
					, #{customer_birth}
					, #{staff_code}
					, NOW())
