mapper.select:
SELECT
*
FROM
	tb_devicein AS i
join tb_device  AS d
	on 	i.device_code = d.device_code
JOIN tb_staff AS s
 	ON i.staff_code = s.staff_code
JOIN tb_store AS a
	ON s.store_code = a.store_code
JOIN tb_deviceinfo as info
	on info.deviceinfo_devicein = i.devicein_code

		
			
			 	
			    
			    	and i.devicein_status = #{searchStatus}
			    	
			    
		   			and i.dealler_code = #{searchDealler}	
		   		
		   		
		   			and i.staff_code = #{searchStaff}
		   		
		   		
		   			and i.devicein_serial = #{searchSerial}
		   		
		   		
		   			and i.devicein_date >= #{searchDateStart}
		   		
		   		
		   			and #{searchDateEnd} >= i.devicein_date
		   		
		    	
		    
		    	
			    
			    	and i.devicein_status = #{searchStatus}
			    					   
		    	
		    
		    	
			    
			    	and i.dealler_code = #{searchDealler}
			    					   
		    
		    
		    	
			    
			    	and i.staff_code = #{searchStaff}
			    					   
		    
		    
		    	
			    
			    	and i.devicein_serial = #{searchSerial}
			    					   
		    
		    
		    	
			    
			    	and i.devicein_date >= #{searchDateStart}
			    
				
			    	and #{searchDateEnd} >= i.devicein_date

mapper.insert:
	
		
 			SELECT 
 				CONCAT('code', MAX(CAST(SUBSTRING(deviceinfo_code,5) AS DECIMAL)+1)) AS infoCode
			FROM tb_deviceinfo
 		
	INSERT		
		into tb_deviceinfo
			(
			deviceinfo_code
			,deviceinfo_devicein
			,deviceinfo_method
			,deviceinfo_store
			,deviceinfo_memo
			,deviceinfo_reg_date
			)			
			values
			(
			#{infoCode}
			,#{deviceinCode}
			,#{infoMethod}
			,#{infoStore}
			,#{deviceinMemo}			
			,now()
			)

mapper.update:

	
		SELECT
			devicein_code as deviceinCode
		FROM
			tb_devicein
		where
			devicein_serial = #{updateSerial}
					
	
		update		
			tb_deviceinfo
		set
			deviceinfo_devicein = #{deviceinCode}
			,deviceinfo_method = #{updateMethod}
			,deviceinfo_store = #{updateStore}
			,deviceinfo_memo = #{updateMemo}
			,deviceinfo_reg_date = NOW()
		where
			deviceinfo_devicein = #{deviceinCode}

		
			SELECT
					device_code as deviceCode
			FROM
					tb_device 
			where
					device_model=#{updateModel}
					and device_type=#{updateType}
					and device_color=#{updateColor}
					and device_price=#{updatePrice}
 		
			update
				tb_devicein
			
			set
				devicein_serial = #{updateSerial}
				,dealler_code = #{updateDealler}
				,device_code = #{deviceCode}
				,devicein_date = NOW()
				,devicein_memo = #{updateMemo}
				,staff_code = #{updateStaff}
				,devicein_reg_date = NOW()
			
			WHERE 
				devicein_serial = #{updateSerial}
