mapper.insert:

	    	select
	    	(case COUNT(*)
 			when 0 then 'store_1'
	    	else concat('store_',Max(cast(SUBSTR(store_code,7) AS DECIMAL))+1)
	    	end) as store_code
	    	from
	    	  	tb_store as s
	 
	INSERT INTO tb_store
			(store_code
			, store_name
			, store_addr
			, store_tel
			, member_id
			, store_reg_date)
	VALUES (#{store_Code}
			,#{store_Name}
			,#{store_Addr}
			,#{store_Tel}
			,#{store_Reg_id}
			, NOW())
